/**
 * Detect Electron renderer process, which is node, but we should
 * treat as a browser.
 */

if (typeof process !== 'undefined' && process.type === 'renderer') {
  module.exports = require('socket.io/node_modules/debug/browser.js.js');
} else {
  module.exports = require('socket.io/node_modules/debug/node.js.js');
}
